import React from 'react';
import styled from 'styled-components';
import Grid from '@material-ui/core/Grid';

const PricingWrapper = styled.section`
  width: 100%;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  padding: 16px;

  @media only screen and (min-width: 960px) {
    padding: 16px 120px;
  }
`;

const PricingTitle = styled.h3`
  font-family: 'Montserrat', sans-serif;
  font-size: 2.5rem;
  font-weight: 800;
  color: #fff;
  background: #f94343;
  margin: 0 auto;
  display: inline-block;
  text-align: center;
`;

const PricingSubtitle = styled.h5`
  font-family: 'Montserrat', sans-serif;
  font-size: 2rem;
  font-weight: 800;
  color: #1f1f1f;
  margin: 0 auto;
  text-align: center;
`;

const PricingGrid = styled(Grid)`
  width: 100%;
  box-sizing: border-box;
`;

const PricingCardContainer = styled.div`
  width: 100%;
  border: 2px solid #1f1f1f;
  border-radius: 8px;
  padding: 16px;
  box-sizing: border-box;
`;

const Pricing = ({}) => (
  <PricingWrapper>
    <PricingTitle>
      Pricing
    </PricingTitle>
    <br/>
    <PricingSubtitle>Where guac is never extra</PricingSubtitle>
    <PricingGrid container spacing={8}>
      <Grid item xs={12} sm={12} md={4} lg={4} xl={4}>
        <PricingCardContainer>
          Appetizer
        </PricingCardContainer>
      </Grid>
      <Grid item xs={12} sm={12} md={4} lg={4} xl={4}>
        <PricingCardContainer primary>
          Entree
        </PricingCardContainer>
      </Grid>
      <Grid item xs={12} sm={12} md={4} lg={4} xl={4}>
        <PricingCardContainer>
          Dessert
        </PricingCardContainer>
      </Grid>
    </PricingGrid>
  </PricingWrapper>
);

export default Pricing;
