import React, { Component } from 'react';
import styled from 'styled-components';
import posed, {PoseGroup} from 'react-pose'
import Dropzone from 'react-dropzone'
import TextField from '@material-ui/core/TextField';

const MenuAdderContainer = styled.div`
  width: 100%;
  max-width: 720px;

`;

const DropzoneContainer = styled.div`
  width: 100%;
  height: 140px;
  border: 2px dashed ${props => props.active ? 'green' : '#9f9f9f'};
  padding: 24px;
  box-sizing: border-box;
  border-radius: 0;
  background: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 16px;
  position: relative;

  & .initiate-dropzone {
    padding: 24px;
    box-sizing: border-box;
      display: flex;
      align-items: center;
      justify-content: center;
    position: absolute;
    width: 100%:
    height: 100%;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
  }
`;

const FilesContainer = styled.div`
  width: 100%;
  display: flex;
  flex-flow: column nowrap;
`;

const PosedFile = posed.div({
  enter: {
    y: 0,
    opacity: 1,
  },
  exit: {
    y: 32,
    opacity: 0,
  },
  preEnter: {
    y: 32,
    opacity: 0,
  }
})

const File = styled((props) => <PosedFile {...props} />)`
  width: 100%;
  height: 64px;
  border: 4px solid #1f1f1f;
  box-sizing: border-box;
  margin-bottom: 8px;
`;

class MenuAdder extends Component {

  render() {
    return (
      <MenuAdderContainer>
        <DropzoneContainer>
          <Dropzone onDrop={(accepted,rejected) => this.props.handleDrop(accepted, rejected)}>
            {({getRootProps, getInputProps, isDragActive}) => {
              return (
                <div
                  {...getRootProps()}
                  className="initiate-dropzone"
                  active={isDragActive}
                >
                  <input {...getInputProps()} />
                  {
                    isDragActive ?
                      <p>Drop files here...</p> :
                      <p>Try dropping some files here, or click to select files to upload.</p>
                  }
                </div>
              )
            }}
          </Dropzone>
        </DropzoneContainer>
        <FilesContainer>
          <PoseGroup preEnterPose="preEnter">
            {this.props.files.map((file, i) =>
              <File key={i}>
                {file.name}
              </File>
            )}
          </PoseGroup>
        </FilesContainer>
      </MenuAdderContainer>
    );
  }

}

export default MenuAdder;
